INSERT INTO `wp_contact_form_7` (`cf7_unit_id`, `title`, `form`, `mail`, `mail_2`, `messages`, `additional_settings`) VALUES
(1, 'dlvs-quote', '<div class="multiple-fields title">\r\n	<label for="title">Title (*) </label>\r\n	<div class="input-field">[select* title  id:title include_blank "Mr" "Mrs" "Miss" "Ms" "Dr"]</div>\r\n</div>\r\n\r\n<div class="multiple-fields first-name">\r\n	<label for="first-name">First Name (*)</label>\r\n	<div class="input-field">[text* first-name id:first-name]</div>\r\n</div>\r\n\r\n<div class="multiple-fields last-name">\r\n	<label for="last-name">Last Name (*) </label>\r\n	<div class="input-field last-name">[text* last-name id:last-name]</div>\r\n</div>\r\n\r\n<div class="clear"></div>\r\n\r\n<label for="job-title">Job Title</label>\r\n<div class="input-field">[text job-title id:job-title]</div>\r\n\r\n\r\n\r\n<label for="organisation">Organisation </label>\r\n<div class="input-field">[text organisation id:organisation]</div>\r\n\r\n\r\n\r\n<label for="phone">Phone no (*)</label>\r\n<div class="input-field">[text* phone id:phone]</div>\r\n\r\n\r\n\r\n<label for="email">E-mail (*)</label>\r\n<div class="input-field">[email* email id:email]</div>\r\n\r\n\r\n\r\n<label for="address">Address and Postcode (*)</label>\r\n<div class="input-field">[textarea* address id:address]</div>\r\n\r\n\r\n\r\n<label for="employees">Number of employees (*)</label>\r\n<div class="input-field">[select* employees id:employees "Less than 60" "60-89" "90-119" "120-149" "150-179" "180-299" "More than 300"]</div>\r\n\r\n\r\n\r\n<label for="vaccination_before">Have you offered a flu vaccination before</label>\r\n<div class="input-field">[select* vaccination_before id:vaccination_before "Yes" "No"]</div>\r\n\r\n\r\n\r\n<label for="message">Any other information you would like us to know:</label>\r\n<div class="input-field">[textarea message id:message]</div>\r\n\r\n\r\n[submit "Send"]', 'a:7:{s:7:"subject";s:17:"Mail from DLVS-UK";s:6:"sender";s:46:"[first-name] [last-name] <admin@konscript.com>";s:4:"body";s:462:"Submitted on [_date] - [_time]\r\nSubmitted by anonymous user: [[wpcf7.remote_ip]]\r\n\r\nSubmitted values are:\r\n\r\nTitle: [title]\r\nFirst Name: [first-name]\r\nLast Name: [last-name]\r\nJob Title: [job-title]\r\nOrganisation: [organisation]\r\nPhone no: [phone]\r\nE-mail: [email]\r\nAddress and Postcode: \r\n[address]\r\n\r\nNumber of employees: [employees]\r\nHave you offered a flu vaccination before: [vaccination_before]\r\n\r\nAny other information you would like us to know:\r\n[message]";s:9:"recipient";s:44:"info@flufighters.org.uk, hm@lifecaregroup.eu";s:18:"additional_headers";s:0:"";s:11:"attachments";s:0:"";s:8:"use_html";s:0:"";}', 'a:8:{s:6:"active";s:1:"1";s:7:"subject";s:23:"Your Flu Fighters Quote";s:6:"sender";s:49:"info@flufighters.org.uk <info@flufighters.org.uk>";s:4:"body";s:1975:"Dear [title] [last-name]\r\n\r\n<strong>Great protection for your team from only £11 per dose of vaccine!</strong>\r\n\r\n<strong>Every year we help our customers get the best value for their investment. You benefit from savings according to volume, and choose the amount of nursing time to meet your needs.</strong>\r\n\r\n<strong>Experience tells us that on average, about 1/3 of employees accept the offer of a free influenza vaccination, and depending on the workforce numbers, that price could be as little as £13.16 including all nursing costs.</strong>\r\n\r\n<strong>You save further because our nursing and medical support is charged by the hour, not by the half day, and our network of experienced nurses are very efficient in vaccine administration.</strong>\r\n\r\n<strong>Working with you to provide a great service:</strong>\r\n\r\nA PERSONALISED QUOTE: We will call you to discuss your individual requirements in more detail such as your anticipated take up, shift patterns (which may involve more than one visit) and the number of locations you would like us to visit. All this will allow us to propose the most cost efficient way of delivering your service whilst at the same time offering advice on how to boost take up. Having done this, we will agree a final quote with you.\r\n\r\nTHE RIGHT NUMBER OF DOSES: You don''t need to confirm doses now. We''ll give you a quote, and 7 days before delivery, we will agree an accurate figure with you of the total number of vaccines you require In this way, you are able to adjust the final number, helping to save you money - or benefit from further volume discount.\r\n\r\n \r\n\r\n<center><img src="http://www.flufighters.org.uk/sites/all/themes/flufighters/images/tablev3.jpg" alt="" /></center> \r\n\r\n \r\n\r\n<center><em>Our aim is to provide a premium service, and for our customers to have a trouble free vaccination session. Check out our <a href="http://about-us/testimonials" target="_blank">testimonial page</a>.</em>\r\n\r\n \r\n\r\n</center>";s:9:"recipient";s:7:"[email]";s:18:"additional_headers";s:0:"";s:11:"attachments";s:0:"";s:8:"use_html";s:1:"1";}', 'a:13:{s:12:"mail_sent_ok";s:43:"Your message was sent successfully. Thanks.";s:12:"mail_sent_ng";s:84:"Failed to send your message. Please try later or contact administrator by other way.";s:17:"akismet_says_spam";s:84:"Failed to send your message. Please try later or contact administrator by other way.";s:16:"validation_error";s:74:"Validation errors occurred. Please confirm the fields and submit it again.";s:12:"accept_terms";s:35:"Please accept the terms to proceed.";s:13:"invalid_email";s:28:"Email address seems invalid.";s:16:"invalid_required";s:24:"This field is mandatory.";s:13:"upload_failed";s:22:"Failed to upload file.";s:24:"upload_file_type_invalid";s:30:"This file type is not allowed.";s:21:"upload_file_too_large";s:23:"This file is too large.";s:23:"upload_failed_php_error";s:38:"Failed to upload file. Error occurred.";s:17:"captcha_not_match";s:31:"Your entered code is incorrect.";s:23:"quiz_answer_not_correct";s:27:"Your answer is not correct.";}', '');
